import{_ as t,c as r,o as n,a2 as s}from"./chunks/framework.Bc4oNHcE.js";const o="/assets/3eef9283-b028-4b06-929b-53f560e444f5.CAmsyQHC.png",a="data:image/png;base64,",A="data:image/png;base64,",k=JSON.parse('{"title":"02矩阵及其计算","description":"","frontmatter":{"layout":"doc","title":"02矩阵及其计算"},"headers":[],"relativePath":"math/线性代数/02矩阵及其计算.md","filePath":"math/线性代数/02矩阵及其计算.md"}'),e={name:"math/线性代数/02矩阵及其计算.md"},l=s('<h1 id="矩阵及其计算" tabindex="-1">矩阵及其计算 <a class="header-anchor" href="#矩阵及其计算" aria-label="Permalink to &quot;矩阵及其计算&quot;">​</a></h1><h2 id="线性方程组" tabindex="-1">线性方程组 <a class="header-anchor" href="#线性方程组" aria-label="Permalink to &quot;线性方程组&quot;">​</a></h2><h3 id="n元非齐次性方程组" tabindex="-1">n元非齐次性方程组 <a class="header-anchor" href="#n元非齐次性方程组" aria-label="Permalink to &quot;n元非齐次性方程组&quot;">​</a></h3><p><img src="'+o+'" alt="n元非齐次性方程组"></p><h3 id="n元齐次性方程组" tabindex="-1">n元齐次性方程组 <a class="header-anchor" href="#n元齐次性方程组" aria-label="Permalink to &quot;n元齐次性方程组&quot;">​</a></h3><p><img src="'+a+'" alt="n元齐次性方程组"></p><blockquote><p>对于n元齐次性方程组来说，一定存在一个零解，但不一定存在非零解</p></blockquote><blockquote><p>对于n元非齐次性方程组来说，一定不存在零解，但不一定存在非零解</p></blockquote><h2 id="矩阵" tabindex="-1">矩阵 <a class="header-anchor" href="#矩阵" aria-label="Permalink to &quot;矩阵&quot;">​</a></h2><blockquote><p>由m * n 个数排成的m行n列数表，称为m行n列矩阵。m*n矩阵</p></blockquote><blockquote><p>实矩阵: 元素是实数的矩阵</p></blockquote><blockquote><p>复矩阵：元素是复数的矩阵</p></blockquote><blockquote><p>n阶矩阵：行数和列数都等于n的矩阵</p></blockquote><blockquote><p>行矩阵[行向量]： 只有一行的矩阵</p></blockquote><blockquote><p>列矩阵[列向量]： 只有一列的矩阵</p></blockquote><blockquote><p>零矩阵： 元素都是零的矩阵</p></blockquote><blockquote><p>同型矩阵： 2个矩阵的行数和列数相等</p></blockquote><blockquote><p>单位矩阵： 对角线上的元素都是1，其他位置的元素为0的矩阵</p></blockquote><h2 id="矩阵的计算" tabindex="-1">矩阵的计算 <a class="header-anchor" href="#矩阵的计算" aria-label="Permalink to &quot;矩阵的计算&quot;">​</a></h2><p>设存在2个矩阵 <strong>A</strong> 和 <strong>B</strong></p><h3 id="矩阵的加法" tabindex="-1">矩阵的加法 <a class="header-anchor" href="#矩阵的加法" aria-label="Permalink to &quot;矩阵的加法&quot;">​</a></h3><blockquote><p>当2个矩阵为同型矩阵时，才可以进行加法运算</p></blockquote><blockquote><p>矩阵的加法满足交换律</p></blockquote><blockquote><p>矩阵和副矩阵的和为零矩阵</p></blockquote><blockquote><p><strong>A</strong> + <strong>B</strong> = <strong>B</strong> + <strong>A</strong></p></blockquote><blockquote><p>( <strong>A</strong> + <strong>B</strong> ) + <strong>C</strong> = <strong>B</strong> + ( <strong>A</strong> + <strong>c</strong> )</p></blockquote><h2 id="数与矩阵的乘法" tabindex="-1">数与矩阵的乘法 <a class="header-anchor" href="#数与矩阵的乘法" aria-label="Permalink to &quot;数与矩阵的乘法&quot;">​</a></h2><blockquote><p>(λμ)<strong>A</strong> = λ(μ<strong>A</strong>)</p></blockquote><blockquote><p>(λ+μ)<strong>A</strong> = λ<strong>A</strong> + μ<strong>A</strong></p></blockquote><blockquote><p>λ( <strong>A</strong> + <strong>B</strong> ) = λ<strong>A</strong> + λ<strong>B</strong></p></blockquote><h3 id="矩阵的加法和乘法统称为矩阵的线性运算" tabindex="-1">矩阵的加法和乘法统称为矩阵的线性运算 <a class="header-anchor" href="#矩阵的加法和乘法统称为矩阵的线性运算" aria-label="Permalink to &quot;矩阵的加法和乘法统称为矩阵的线性运算&quot;">​</a></h3><h2 id="矩阵与矩阵相乘" tabindex="-1">矩阵与矩阵相乘 <a class="header-anchor" href="#矩阵与矩阵相乘" aria-label="Permalink to &quot;矩阵与矩阵相乘&quot;">​</a></h2><blockquote><p>定义 设<strong>A</strong>=(a<sub>ij</sub>)是一个m * s的矩阵,<strong>B</strong>=(b<sub>ij</sub>)是一个s * n的矩阵,那么规定矩阵<strong>A</strong>和矩阵<strong>B</strong>的乘积是一个m * n的矩阵，记做<strong>C</strong> = <strong>AB</strong></p></blockquote><blockquote><p>只有当第一个矩阵(左矩阵)的列数等于第二个矩阵(右矩阵)的行数时，2个矩阵才可以相乘</p></blockquote><blockquote><p>矩阵的乘法不满足交换律</p></blockquote><blockquote><p>(<strong>AB</strong>)<strong>C</strong> = <strong>A</strong>(<strong>BC</strong>)</p></blockquote><blockquote><p>(λ)<strong>AB</strong> = (λ<strong>A</strong>)<strong>B</strong> = <strong>A</strong>(λ<strong>B</strong>)</p></blockquote><blockquote><p><strong>A</strong>( <strong>B</strong> + <strong>C</strong> ) = <strong>AB</strong> + <strong>AC</strong></p></blockquote><h2 id="矩阵的转置" tabindex="-1">矩阵的转置 <a class="header-anchor" href="#矩阵的转置" aria-label="Permalink to &quot;矩阵的转置&quot;">​</a></h2><blockquote><p>把矩阵<strong>A</strong>的行换成同系数的列得到的一个新矩阵，叫<strong>A</strong>的转置矩阵记做 <strong>A</strong><sup>T</sup></p></blockquote><blockquote><p><strong>A</strong><sup>T</sup>)<sup>T</sup> = <strong>A</strong></p></blockquote><blockquote><p>( <strong>A</strong> + <strong>B</strong> )<sup>T</sup> = <strong>A</strong><sup>T</sup> + <strong>B</strong><sup>T</sup></p></blockquote><blockquote><p>(λ<strong>A</strong>)<sup>T</sup> = λ<strong>A</strong><sup>T</sup></p></blockquote><blockquote><p>(<strong>AB</strong>)<sup>T</sup> = <strong>B</strong><sup>T</sup><strong>A</strong><sup>T</sup></p></blockquote><h3 id="对称矩阵" tabindex="-1">对称矩阵 <a class="header-anchor" href="#对称矩阵" aria-label="Permalink to &quot;对称矩阵&quot;">​</a></h3><blockquote><p>满足<strong>A</strong><sup>T</sup> = <strong>A</strong></p></blockquote><blockquote><p>元素以对角线为对称轴对应相等</p></blockquote><h2 id="逆矩阵" tabindex="-1">逆矩阵 <a class="header-anchor" href="#逆矩阵" aria-label="Permalink to &quot;逆矩阵&quot;">​</a></h2><h3 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h3><blockquote><p>对于n阶矩阵<strong>A</strong>，如果存在一个矩阵<strong>B</strong>使 <strong>AB</strong> = <strong>E</strong>[单位矩阵]，那么矩阵<strong>A</strong>就是可逆的，<strong>B</strong> 称为矩阵<strong>A</strong>的逆矩阵，简称逆阵</p></blockquote><h2 id="奇异矩阵-当-a-0-时-该矩阵为奇异矩阵-否则为非奇异矩阵" tabindex="-1">奇异矩阵：当|A| = 0 时，该矩阵为奇异矩阵，否则为非奇异矩阵 <a class="header-anchor" href="#奇异矩阵-当-a-0-时-该矩阵为奇异矩阵-否则为非奇异矩阵" aria-label="Permalink to &quot;奇异矩阵：当|A| = 0 时，该矩阵为奇异矩阵，否则为非奇异矩阵&quot;">​</a></h2><h3 id="定理" tabindex="-1">定理 <a class="header-anchor" href="#定理" aria-label="Permalink to &quot;定理&quot;">​</a></h3><blockquote><p>若矩阵<strong>A</strong>可逆，则|A| ≠ 0</p></blockquote><h2 id="克拉默法则" tabindex="-1">克拉默法则 <a class="header-anchor" href="#克拉默法则" aria-label="Permalink to &quot;克拉默法则&quot;">​</a></h2><p><img src="'+o+'" alt="线性方程组"></p><blockquote><p>线性方程组的系数矩阵A的行列式不等于0，则方程组存在唯一解。</p></blockquote><p><img src="'+A+'" alt="线性方程组的解"></p><blockquote><p>其中<strong>A</strong><sub>j</sub>为把系数矩阵<strong>A</strong>中的第j列的元素用线性方程组的常数矩阵替换后得到的n阶矩阵</p></blockquote><blockquote><p>根据可逆矩阵可知，方程的解为系数矩阵的逆矩阵和常数矩阵的乘积</p></blockquote><h2 id="矩阵分块法" tabindex="-1">矩阵分块法 <a class="header-anchor" href="#矩阵分块法" aria-label="Permalink to &quot;矩阵分块法&quot;">​</a></h2><h3 id="定义-1" tabindex="-1">定义 <a class="header-anchor" href="#定义-1" aria-label="Permalink to &quot;定义&quot;">​</a></h3><blockquote><p>将矩阵<strong>A</strong>用若干条横线和若干条纵线分成许多个小矩阵每一个小矩阵称为<strong>A</strong>的子块，以子块为元素的形式上的矩阵称为分块矩阵</p></blockquote>',62),g=[l];function u(c,q,b,h,p,i){return n(),r("div",null,g)}const B=t(e,[["render",u]]);export{k as __pageData,B as default};
