import{_ as e,c as t,o as a,a2 as o}from"./chunks/framework.Bc4oNHcE.js";const u=JSON.parse('{"title":"美团技术架构","description":"","frontmatter":{"layout":"doc","title":"美团技术架构"},"headers":[],"relativePath":"web/前端工程化/美团技术架构.md","filePath":"web/前端工程化/美团技术架构.md"}'),r={name:"web/前端工程化/美团技术架构.md"},l=o('<h2 id="什么是技术架构" tabindex="-1">什么是技术架构 <a class="header-anchor" href="#什么是技术架构" aria-label="Permalink to &quot;什么是技术架构&quot;">​</a></h2><blockquote><p>技术架构是指社会中各种技术之间相互作用、相互联系、按一定目的、一定结构方式组成的技术整体 最终目的是在确保线上的质量和稳定性的前提下，提升团队整体的研发效率</p></blockquote><h2 id="前端工程师如何成长" tabindex="-1">前端工程师如何成长 <a class="header-anchor" href="#前端工程师如何成长" aria-label="Permalink to &quot;前端工程师如何成长&quot;">​</a></h2><blockquote><p><strong>做规划</strong> 向前看，它是对未来整体性、长期性、基本性问题的思考和考量，设计未来整套行动的方案 <strong>做复盘</strong> 向后看，它指的是我们从过去的经验和实际工作中进行学习，帮助大家有效地总结经验，提升能力的方式 <strong>保持视野</strong> 我们的思想或者知识的领域。但是视野并不是一成不变的，因为世界在不断地变化，我们不能闭门造车，要拥抱变化，要以此来调整自己发展的方向</p></blockquote><h3 id="如何保持视野" tabindex="-1">如何保持视野 <a class="header-anchor" href="#如何保持视野" aria-label="Permalink to &quot;如何保持视野&quot;">​</a></h3><ol><li>核心关注圈，就是我们在团队的业务当中可能会每天都用到的，或者说自己感兴趣的，又或者说在业界非常火的东西，我们要保持高度的关注</li><li>一般关注圈，就是我们在团队的业务当中可能不常用到，但以后很有可能会用到的，或者说自己有点感兴趣的，又或者说我们能预判业界之后有可能会火的东西，我们要保持一定的关注度</li><li>扫盲关注圈，就是我们可能业务中不会用到，自己也不太感兴趣，业界可能也不太火的，我们不需要放太多的精力去关注它，只需要知道它大概是做什么的，解决了什么问题，就足够了</li></ol><p><a href="https://mp.weixin.qq.com/s/9hvpc71YNMW6bbJZTyoSXw" target="_blank" rel="noreferrer">文章引用</a></p>',7),n=[l];function s(i,_,c,h,d,p){return a(),t("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
